import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Book } from 'src/app/interfaces/book';
import { BookService } from 'src/app/services/book.service';

@Component({
  selector: 'app-show-book',
  templateUrl: './show-book.component.html',
  styleUrls: ['./show-book.component.css']
})
export class ShowBookComponent {
	public book!: Book;
	constructor(
		private service: BookService,
        private router: Router,
	) { }
	deleteBook(bookId: number) {
		this.service.deleteBook(bookId).subscribe(() => {
            this.router.navigate(['/books']);
        });

        // this.router.navigate(['/books'], { queryParams: { bookId: bookId } });
        // this.router.navigate(['/books'], { queryParamsHandling: 'merge' });
        // this.router.navigate(['/books'], { fragment: 'section1' });
        // this.router.navigate(['/books'], { relativeTo: this.route });
        // this.router.navigateByUrl('/books');
        // this.router.navigate(['/books', bookId], { preserveQueryParams: true });
        // this.router.navigate(['/books', bookId], { skipLocationChange: true });
	}
}
