import { Component } from '@angular/core';
import { Book } from 'src/app/interfaces/Book';

@Component({
  selector: 'app-new-book',
  templateUrl: './new-book.component.html',
  styleUrls: ['./new-book.component.css']
})
export class NewBookComponent {

}
<h1>Add a new book</h1>
<!-- id: number;
	title: string;
	author: string;
	description: string;
	rate: number;
	dateStart: Date;
	dateRead: Date; -->
<form (ngSubmit)="onSubmit()">
	<div class="form-group">
        <label for="title">Title:</label>
        <input type="text" class="form-control" id="title" [(ngModel)]="book.title" required>
    </div>
    <div class="form-group">
        <label for="author">Author:</label>
        <input type="text" class="form-control" id="author" [(ngModel)]="book.author" required>
    </div>
    <div class="form-group">
        <label for="description">Description:</label>
        <textarea class="form-control" id="description" rows="3" [(ngModel)]="book.description"></textarea>
    </div>
	<div class="form-group">
        <label for="rate">Rating:</label>
        <input type="number" class="form-control" id="rate" min="1" max="10" [(ngModel)]="book.rate" required>
    </div>
	<div class="form-group">
        <label for="dateStart">Start date:</label>
        <input type="date" class="form-control" id="dateStart" [(ngModel)]="book.dateStart">
    </div>
</form>
